% Example_2cr05_contour_plot_rad.m
% Nasser June 12, 2019
clear;clc
% The data in the files:
% x2cr05_0_pt.mat, y2cr05_0_pt.mat, R2cr05_0_pt.mat,
% which has been generated by the MATLAB file:
% Example_2cr05_contour_save_rad.m
% to plot the contour lines in Figure 5 (first row, center) in the paper:
% E. Kalmoun, M. Nasser and M. Vuorinen, Numerical computation of Mityuk's
% function and radius for some canonical slit domains. 
% 
%
% The parametrization of the boundary
n  =  2^14;
t  = [0:2*pi/n:2*pi-2*pi/n].';
k  =  1; % the unit circle: |z|=1
et(1+(k-1)*n:k*n,1)  =  1.* exp(i.*t);
k  =  2; % the inner circle: |z-0.05|=0.25
et(1+(k-1)*n:k*n,1)  =  0.05+0.25.* exp(-i.*t);
% loading the data files
x=load('x2cr05_0_pt.mat','-ascii');
y=load('y2cr05_0_pt.mat','-ascii');
R=load('R2cr05_0_pt.mat','-ascii');
% plot the contour lines
figure
R(x.^2+y.^2>0.99)=NaN;
tv  = [0.12,0.25,0.37,0.5,0.65,0.8,1,1.3,2.5]; %level values
hold on
box on
contour(x,y,R,tv,'Color','b','LineWidth',1.0);
for k=1:2
    crv = et(1+(k-1)*n:k*n,1);crv(end+1)=crv(1);
    plot(real(crv),imag(crv),'k','LineWidth',1.5)
end

set(gca,'XTick',[-3:1:3],'FontSize',15);
set(gca,'YTick',[-3:1:3]);
axis equal
axis([-1.01 1.01 -1.01 1.01])
set(gca,'LooseInset',get(gca,'TightInset'))
% axis off
% print -depsc fig_2cr_0R3
% print -dpdf  fig_2cr_0R3
